// ReSharper disable All

namespace MyTelegram.Schema;

///<summary>
/// Info about chat invites generated by admins.
/// See <a href="https://corefork.telegram.org/constructor/ChatAdminWithInvites" />
///</summary>
[JsonDerivedType(typeof(TChatAdminWithInvites), nameof(TChatAdminWithInvites))]
public interface IChatAdminWithInvites : IObject
{
    ///<summary>
    /// The admin
    ///</summary>
    long AdminId { get; set; }

    ///<summary>
    /// Number of invites generated by the admin
    ///</summary>
    int InvitesCount { get; set; }

    ///<summary>
    /// Number of revoked invites
    ///</summary>
    int RevokedInvitesCount { get; set; }
}
