// ReSharper disable All

namespace MyTelegram.Schema.Messages;

///<summary>
/// Contains information about a chat export file, generated by a foreign chat app.
/// See <a href="https://corefork.telegram.org/constructor/messages.HistoryImportParsed" />
///</summary>
[JsonDerivedType(typeof(THistoryImportParsed), nameof(THistoryImportParsed))]
public interface IHistoryImportParsed : IObject
{
    ///<summary>
    /// Flags, see <a href="https://corefork.telegram.org/mtproto/TL-combinators#conditional-fields">TL conditional fields</a>
    ///</summary>
    int Flags { get; set; }

    ///<summary>
    /// The chat export file was generated from a private chat.
    ///</summary>
    bool Pm { get; set; }

    ///<summary>
    /// The chat export file was generated from a group chat.
    ///</summary>
    bool Group { get; set; }

    ///<summary>
    /// Title of the chat.
    ///</summary>
    string? Title { get; set; }
}
